mapper.select:
SELECT userCode,userName,valueName AS userTypeName,userPassword 
FROM backend_user
INNER JOIN data_dictionary ON data_dictionary.valueId=backend_user.userType

	 and userCode=#{userCode}
	 and userPassword=#{userPassword}

and typeName='用户类型'

SELECT app_info.id,softwareName,APKName,softwareSize,dic2.valueName AS flatformName, app1.categoryName as categoryLevel1Name,app2.categoryName as categoryLevel2Name, app3.categoryName as categoryLevel3Name,dic1.valueName AS statusName,downloads,versionNo, app_info.id,versionId,status FROM app_info INNER JOIN app_version ON app_info.versionId=app_version.id INNER JOIN app_category AS app1 ON app1.id=app_info.categoryLevel1 INNER JOIN app_category AS app2 ON app2.id=app_info.categoryLevel2 INNER JOIN app_category AS app3 ON app3.id=app_info.categoryLevel3 INNER JOIN data_dictionary AS dic1 ON app_info.status=dic1.valueId AND dic1.typeCode='APP_STATUS' INNER JOIN data_dictionary AS dic2 ON app_info.flatformId=dic2.valueId AND dic2.typeCode='APP_FLATFORM'
and softwareName LIKE concat('%',#{softwareName},'%') and flatformId=#{flatformId} and categoryLevel1=#{categoryLevel1} and categoryLevel2=#{categoryLevel2} and categoryLevel3=#{categoryLevel3}
limit #{pageIndex},#{pageSize}

SELECT COUNT(1)
FROM app_info
INNER JOIN app_category AS app1 ON app1.id=app_info.categoryLevel1
INNER JOIN app_category AS app2 ON app2.id=app_info.categoryLevel2
INNER JOIN app_category AS app3 ON app3.id=app_info.categoryLevel3
INNER JOIN data_dictionary AS dic1 ON app_info.status=dic1.valueId AND dic1.typeCode='APP_STATUS'
INNER JOIN data_dictionary AS dic2 ON app_info.flatformId=dic2.valueId AND dic2.typeCode='APP_FLATFORM'
INNER JOIN app_version ON app_info.versionId=app_version.id

	 and softwareName LIKE concat('%',#{softwareName},'%')
	 and flatformId=#{flatformId}
	 and categoryLevel1=#{categoryLevel1}
	 and categoryLevel2=#{categoryLevel2}
	 and categoryLevel3=#{categoryLevel3}
